Rsvps = new Mongo.Collection("rsvps");

Rsvps.attachSchema(new SimpleSchema({
  name: {
    type: String,
    label: "First Name *",
    optional: false, 
    min: 5,
    max: 80    
  },
  last: {
    type: String,
    label: "Last Name *",

    min: 5,
    max: 80    
  },
organization: {
    type: String,
    label: "Company Name *",

    min: 5,
    max: 80    
  },
  website: {
    type: String,
    label: "Company website *",

    min: 5,
    max: 80    
  },
  createdAt: {
    type: Date,
    autoValue: function () {
      return new Date();
    }
  }
}));

Rsvps.allow({
  insert: function (userId, doc) {
    return true;
  },
  update: function (userId, doc, fields, modifier) {
    return true;
  },
  remove: function (userId, doc) {
    return true;
  }
});
