Rsvps = new Mongo.Collection("rsvps");

Rsvps.attachSchema(new SimpleSchema({
  name: {
    type: String,
    label: "First Name *",
    optional: false, 
    min: 2,
    max: 80    
  },
  last: {
    type: String,
    label: "Last Name *",
    optional: false, 
    min: 3,
    max: 80    
  },
organization: {
    type: String,
    label: "Company Name *",
    optional: false, 
    min: 5,
    max: 80    
  },
  website: {
    type: String,
    label: "Company website *",
    optional: false, 
    min: 5,
    max: 80    
  },
    email: {
    type: String,
    label: "Email *",
    optional: false, 
    min: 5,
    max: 80    
  },
   phone: {
    type: Number,
    label: "Phone *",
    optional: false 
  },
  industry: {
    type: String,
    label: "What industry/category is your company in? *",
    optional: false 
  },
  Competitors: {
    type: String,
    label: "Which companies do you consider your main competitors? *",
    optional: false 
  },
  iadSense: {
      type: String,
      optional: false,
     label: "How did you hear about Berguer PR?",
      autoform: {

        options: [
            {
               label: "Prior Client",
               value: "Prior Client"
            },
            {
               label: "Referral",
               value: "Referral"
            },
            {
               label: "Our Website",
               value: "Our Website"
            },
             {
               label: "News Article",
               value: "News Article"
            },
             {
               label: "Other",
               value: "Other"
            }
         ]
      }
   },
   pr: {
    type: String,
    optional: false,
    label: "Full Service",
    autoform: {
      afFieldInput: {
        type: "boolean-checkbox"
      }
    }
  },
     dev: {
      type: String,
      optional: true,
     label: "What is the development stage of your company or product?",
      autoform: {

        options: [
            {
               label: "Pre-Launch",
               value: "Pre-Launch"
            },
            {
               label: "Launched within last 12 months",
               value: "Launched within last 12 months"
            },
            {
               label: "In the market over 12 months",
               value: "In the market over 12 months"
            }
         ]
      }
   },
  createdAt: {
    type: Date,
    autoValue: function () {
      return new Date();
    }
  }
}));

Rsvps.allow({
  insert: function (userId, doc) {
    return true;
  },
  update: function (userId, doc, fields, modifier) {
    return true;
  },
  remove: function (userId, doc) {
    return true;
  }
});
