Rsvps = new Mongo.Collection("rsvps");

Rsvps.attachSchema(new SimpleSchema({
  name: {
    type: String,
    label: "이름 *",
    optional: false, 
    min: 2,
    max: 80    
  },
  last: {
    type: String,
    label: "성 *",
    optional: false, 
    min: 2,
    max: 80    
  },
organization: {
    type: String,
    label: "사업명 *",
    optional: false, 
    min: 2,
    max: 80    
  },
  website: {
    type: String,
    label: "사업 웹사이트 주소",
    optional: false, 
    min: 4,
    max: 80    
  },
    email: {
    type: String,
    label: "이매일 주소 *",
    optional: false, 
    min: 5,
    max: 80    
  },
  budget: {
    type: String,
    label: "예측 재정",
    optional: false, 
    min: 1,
    max: 80    
  },
   phone: {
    type: Number,
    label: "전화번호 *",
    optional: false 
  },
  industry: {
    type: String,
    label: "What industry/category is your company in? *",
    optional: false 
  },
  Competitors: {
    type: String,
    label: "Which companies do you consider your main competitors? *",
    optional: false 
  },
  iadSense: {
      type: String,
      optional: false,
     label: "How did you hear about Berguer PR?",
      autoform: {

        options: [
            {
               label: "Prior Client",
               value: "Prior Client"
            },
            {
               label: "Referral",
               value: "Referral"
            },
            {
               label: "Our Website",
               value: "Our Website"
            },
             {
               label: "News Article",
               value: "News Article"
            },
             {
               label: "Other",
               value: "Other"
            }
         ]
      }
   },

     whatElse: {
      type: String,
      optional: true,
      label: "What else should we know?",
      min: 20,
      max: 1000,
      autoform: {
         rows: 5
      }
   },
   another: {
    type: Boolean,
    optional: false,
    autoform: {
     type: "switch",
      trueLabel: "Online",
      falseLabel: "Offline"
    }
  },

  createdAt: {
    type: Date,
    autoValue: function () {
      return new Date();
    }
  }
}));

Rsvps.allow({
  insert: function (userId, doc) {
    return true;
  },
  update: function (userId, doc, fields, modifier) {
    return true;
  },
  remove: function (userId, doc) {
    return true;
  }
});
